    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        double payH, payM, hours;
        String name;
        
        name = String.valueOf(txtName.getText());
        payH = Double.parseDouble(txtPay.getText());
        hours = Double.parseDouble(txtHours.getText());
        
        payM = payH * hours;
        
        txtA.setText(String.valueOf(name));
        
        if (payM > 450000){
            txtB.setText(String.valueOf(payM));
        }
        else{
            txtB.setText("");
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

